import React from "react";
import lady_on_phone from "./lady_on_phone.svg";
import "./App.css";

import quotes from "./quotes";
import Affirmation from "./Affirmation";

function getRandomInt(max) {
  return Math.floor(Math.random() * Math.floor(max));
}

function generateQuote() {
  const quoteIndex = getRandomInt(quotes.length);
  const quote = {
    author: quotes[quoteIndex].author,
    quote: quotes[quoteIndex].quote
  };
  return quote;
}

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      quote: generateQuote()
    };

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    this.setState({ quote: generateQuote() });
  }

  render() {
    const quote = this.state.quote;
    return (
      <div className="App">
        <Affirmation />
        <header className="App-header">
          <h1>Affirmation Generator</h1>
          <img src={lady_on_phone} className="App-logo" alt="logo" />
          <blockquote className="quote">{quote.quote}</blockquote>
          <p className="author-citation">{quote.author}</p>
          <button onClick={this.handleClick}>Next Affirmation</button>
        </header>
      </div>
    );
  }
}

export default App;
