import React from "react";
import logo from "./logo.svg";
import "./App.css";

const randomInt = Math.floor(Math.random(quotes.length));

console.log(randomInt);

const quotes = [
  {
    words:
      "I will not bow down to somebody else's whim or to someone else's ignorance.",
    source: "unknown",
    author: "bell hooks",
    shownAlready: False
  },
  {
    words:
      "there is you and you.\nthis is a relationship.\nthis is the most important relationship.",
    source: "home",
    author: "nayyirah waheed",
    shownAlready: False
  },
  {
    words:
      "Caring for myself is not self-indulgence, it is self-preservation, and that is an act of political warfare.",
    source: "unknown",
    author: "Audre Lorde",
    shownAlready: False
  },
  {
    words:
      "The sunlight isn't the only thing that makes me happy anymore now that I can endure my storms.",
    source: "Words from a Wanderer",
    author: "Alex Elle",
    shownAlready: False
  },
  {
    words:
      "Life is so amazing and ridiculous. It just makes you want to laugh out loud at all the things you thought would destroy you.",
    source: "twitter",
    author: "Alysia Harris",
    shownAlready: False
  },
  {
    words:
      "The only way of finding the limits of the possible is by going beyond them into the impossible.",
    author: "Arthur C Clarke",
    source: "unknown",
    shownAlready: False
  },
  {
    words:
      "Out of suffering have emerged the strongest souls; the most massive characters are seared with scars.",
    author: "Khalil Gibran",
    source: "unknown",
    shownAlready: False
  },
  {
    words:
      "The reason we struggle with insecurity is because we compare our behind-the-scenes with everyone else’s highlight reel.",
    author: "Unknown",
    source: "unknown",
    shownAlready: False
  },
  {
    words: "Don't let your fears overwhelm your desire.",
    author: "Unknown",
    source: "unknown",
    shownAlready: False
  },
  {
    words:
      "The moment you feel like you have to prove your worth to someone is the moment to absolutely and utterly walk away.",
    source: "unknown",
    author: "Alysia Harris",
    shownAlready: False
  },
  {
    words:
      "One day the universe will curl up beside you and begin to purr. Don't worry. You'll know exactly where to place your hands.",
    source: "unknown",
    author: "Alysia Harris",
    shownAlready: False
  },
  {
    words:
      "I love myself.\nthe\nquietest,\nsimplest,\nmost\npowerful\nrevolution\never.",
    source: "home",
    author: "nayyirah waheed",
    shownAlready: False
  },
  {
    words:
      "she is water\n soft enough\n to offer life\n tough enough\n to drown it away",
    source: "unnown",
    author: "rupi kaur",
    shownAlready: False
  }
];

const quote = {
  author: quotes[randomInt].author,
  quote: quotes[randomInt].quote
};
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p className="quote">{quote.quote}</p>
        <p className="author-citation">{quote.author}</p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
